const openModal=document.querySelector("#open-modal"),modal=document.querySelector(".modal"),closeModal=document.querySelector(".close-modal");openModal.onclick=()=>{modal.style.display="block"},closeModal.onclick=()=>{modal.style.display="none"},window.onclick=o=>{o.target===modal&&(modal.style.display="none")},document.addEventListener("keydown",function(o){"Escape"===o.key&&(modal.style.display="none")});