const openModal=document.querySelector("#open-modal"),modal=document.querySelector(".modal"),closeModal=document.querySelector(".close-modal");openModal.onclick=function(){modal.style.display="block",console.log("Modal was opened")},closeModal.onclick=function(){modal.style.display="none",console.log("Modal was closed")},window.onclick=function(o){o.target===modal&&(modal.style.display="none")};